import React from 'react'
import * as axios from "axios";


class ResouceDetails extends React.Component {
    state={
        details: {}
    }

    componentDidMount() {
        console.log('Создалась')

        axios.get(this.props.state.actualResourceUrl).then(resp=>{
            this.setState({
                details: resp.data
            })
        })
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps!=this.props)
        {
            console.log('Компонента обновилась')
            axios.get(this.props.state.actualResourceUrl).then(resp=>{
                this.setState({
                    details: resp.data
                })
            })
        }
    }


    render() {
        console.log(this.state)
        return (<>
            <h3>{this.props.state.actualResourceName}</h3>
            <div>Привет</div>
            </>
        )
    }
}

export default ResouceDetails
