import * as axios from 'axios'


const instance=axios.create({
    baseURL: 'https://swapi.dev/api/'
})

export const swAPI={
    getPlanets: ()=>instance.get('planets/'), //Получение списка планет
    getPlanetDetails: (id)=>instance.get(`planets/${id}`), // Получение деталей выбранной планеты (по id)
    getResources: ()=>instance.get(), //Получение ресурсов из root
    getDetailsSelectedResource: (resource)=>instance.get(`/${resource}`) //Получение деталей выбранного ресурса
}
