import React from 'react'
import {connect} from "react-redux";
import {getRootResource} from "../../redux/statistic-reducer";
import './statistics.css'
import ResouceDetails from "./ResouceDetails/resouceDetails";

class Statistics extends React.Component {
    state={
        actualResource: null,
        editMode: false
    }
    componentDidMount() {
        getRootResource()
    }

    render() {
        console.log(this.state)
        let arr=[]
        for (let i in this.props.rootResource) {
            arr.push(
                <><div className={'itemPlanet'} onClick={
                    ()=>{
                        this.setState({
                            actualResource: this.props.rootResource[i],
                            editMode: true
                        })
                    }
                }>{i}</div></>
            )
        }
        return (
            <div className="statistics">
                <div>{arr}</div>
                <div>{this.state.actualResource && <ResouceDetails />}</div>

            </div>
        )
    }
}

let mapStateToProps=(state)=>({
    rootResource: state.statistics.rootResource
})

export default connect(mapStateToProps,getRootResource)(Statistics);
