import React from 'react'
import {connect} from "react-redux";
import {getRootResource} from "../../redux/statistic-reducer";

class Statistics extends React.Component {
    state={
        actualResource: null
    }
    componentDidMount() {
        getRootResource()
    }

    render() {
        let arr=[]
        for (let i in this.props.rootResource) {
            arr.push(
                <><div onClick={
                    ()=>{
                        this.setState({
                            actualResource: this.props.rootResource[i]
                        })
                    }
                }>{i}</div></>
            )
        }
        return (
            <div className="statistics">
                {arr}
            </div>
        )
    }
}

let mapStateToProps=(state)=>({
    rootResource: state.statistics.rootResource
})

export default connect(mapStateToProps,getRootResource)(Statistics);
